import React, { useRef, useEffect, Fragment, useState } from 'react';
import Palette from '../Palette/Palette';
import Button from '../Buttons';
import LineWidthBar from '../LineWidthBar';
import GalleryItem from '../addNewImage';
import DeleteButton from '../DeleteButton';
import LoadImage from '../LoadImage';

import styles from '../Gallery.module.css';


const Canvas = props => {
    let [color, setColor] = useState('black');
    let [gallery, setImage] = useState([]);
    let [paths, setPath] = useState([]);
    let [lineWidth, setLineWidth] = useState(2);
    let [toggleItem, setToggle] = useState([]);
    const canvasRef = useRef(null);
    const draw = (canvas, color, mode) => {
        let rememberPath = false;
        let points = [];
        let context = canvas.getContext('2d');
        context.globalCompositeOperation = mode ==='line'? "source-over": "destination-out";
        canvas.onmousemove = function drawIfPressed (e) {
            let x = e.offsetX;
            let y = e.offsetY;
            let dx = e.movementX;
            let dy = e.movementY;
            context.lineCap = "round";
            context.lineWidth = lineWidth;
            if (e.buttons > 0) {
              context.beginPath();
              context.moveTo(x, y);
              context.lineTo(x - dx, y - dy);
              context.stroke();
              context.strokeStyle = color;
              context.closePath();
            }
            if(rememberPath){
                points.push({x:x-dx, y:y-dy, color: color, lineWidth: lineWidth})
            }
        };
        canvas.onmouseup = (e) => {//не нажата
            setPath([...paths, points])
            rememberPath = false;
        };
        canvas.onmousedown = (e)=>{//нажата
            let x = e.offsetX;
            let y = e.offsetY;
            let dx = e.movementX;
            let dy = e.movementY;
            points = [];
            rememberPath = true;
            points.push({x:x-dx,y:y-dy})
        };
    }

    const drawPaths = (canvas)=> {
        let context = canvas.getContext('2d');
        context.clearRect(0,0,canvas.width,canvas.height);
        paths.forEach(path=>{
        context.beginPath();
        context.moveTo(path[0].x,path[0].y); 
        for(let i = 1; i < path.length; i++){
            context.lineTo(path[i].x,path[i].y); 
            context.strokeStyle = path[i].color;
            context.lineWidth = path[i].lineWidth;
        }
        context.stroke();
        })
    };

    const showGallery = () => {
        if(localStorage.length !==0){
            for(let j = 1; j <= localStorage.length; j++){
                let dataURL = localStorage.getItem(`gallery${j}`);
                addNewImg(dataURL); 
            }
        } 
    };

    const onChangeLineWidth = width => setLineWidth(width); 
    const onChangeColor = color => setColor(color);
    const save = () => {
        const canvas = canvasRef.current;
        let size = localStorage.length;
        let dataURL = canvas.toDataURL("image/png", 1.0);
        if(localStorage.getItem(`gallery${size}`) !== dataURL){//картинку сохраняем один раз
            localStorage.setItem(`gallery${size+1}`, dataURL)
            addNewImg(dataURL); 
        }
    };
    const clear = () => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
        setPath([]);
    };
    const eraser = () => {
        const canvas = canvasRef.current
        draw(canvas,"rgb(255, 255, 255, 1)",'eraser');
    };
    const undo = () => {
        const canvas = canvasRef.current;
        paths.splice(-1,1);
        drawPaths(canvas);
    }




    const toggleStyle = (value) => {
        setToggle(value);
    };
    const addNewImg = (dataURL) => {
       setImage([...gallery, dataURL]);
    } 
    const onDelete= (dataURL) => setImage(prevActions => (
        prevActions.filter((value, i) => value !== dataURL)
      ));

    useEffect(() => {
        //showGallery();
        const canvas = canvasRef.current;
        draw(canvas, color,'line');
    }, [draw, color, gallery]);
//, lineWidth, toggleStyle

  return <Fragment>
    <div className={styles.gallery}>{
        gallery.map((item, index)=><div className={styles.block} key={item}>
        <GalleryItem key={item+index}  src={item}  onClick={toggleStyle} active={(item) === toggleItem}/>
        <DeleteButton id={item} onClick={onDelete} className={styles.deleteButton}/>
        </div>)
    }</div>
    <LoadImage addNewImg={addNewImg} />
    <canvas className={styles.canvas} width='450' height='450' ref={canvasRef} {...props}/>
    <LineWidthBar onChangeLineWidth={onChangeLineWidth} value={lineWidth}/> 
    <div>
        <Button onClick={save} id={'save'} text={'сохранить'}/>
        <Button onClick={eraser} id={'eraser'} text={'ластик'}/>
        <Button onClick={clear} id={'clear'} text={'clear'}/>
        <Button onClick={undo} id={'undo'} text={'undo'}/>
    </div>
    <Palette onChangeColor={onChangeColor}></Palette>
  </Fragment> 
}
export default Canvas;
