import { useEffect } from "react";
import React, { useRef, useState } from 'react';


import styles from './Camera.module.css';

function addScript( url) {

        const script = document.createElement('script');

        script.src = url;
        script.async = true;
        document.body.appendChild(script);

}; 
const Camera = props => {
    const canvasRef = useRef(null);
    const videoRef = useRef(null);
    let model, w, h;



    useEffect(()=>{
        const canvas = canvasRef.current;
        const video = videoRef.current;
        addScript("https://cdn.jsdelivr.net/npm/@tensorflow/tfjs");
            addScript("https://cdn.jsdelivr.net/npm/@tensorflow/tfjs-converter")
            addScript("https://cdn.jsdelivr.net/npm/@tensorflow-models/facemesh")
            addScript("https://cdn.jsdelivr.net/npm/three@0.116.1/build/three.min.js")
        async function fetchCam () {

            const stream = await navigator.mediaDevices.getUserMedia({
				video: true,
				audio: false
			});
            video.srcObject = stream;
            await new Promise(function (res) {
				video.onloadedmetadata = function () {
					//w = video.videoWidth;
					//h = video.videoHeight;
					res();
				}
			});

            /*
             <script src="https://cdn.jsdelivr.net/npm/@tensorflow/tfjs"></script>
	<!-- <script src="lib/tf-js-core.js"></script> -->
	<!-- <script src="https://cdn.jsdelivr.net/npm/@tensorflow/tfjs-converter"></script> -->
	<!-- <script src="lib/tf-js-converter.js"></script> -->
	<script src="https://cdn.jsdelivr.net/npm/@tensorflow-models/facemesh"></script>
	<!-- <script src="lib/tf-js-facemesh.js"></script> -->
	<script src="https://cdn.jsdelivr.net/npm/three@0.116.1/build/three.min.js"></script>
             */


			//video.height = h;
			//video.width = w;
        }
        fetchCam()
        video.play();


    }, []);
    return <div className={styles.camera}>
        <div>Camera</div>
        <video ref={videoRef} muted={true} playsInline={true}  className={styles.webcam} autoPlay={true} ></video>
        <canvas className={styles.faceCanvas} tabIndex="1"  ref={canvasRef} {...props}></canvas>
    </div>;
};
export default Camera;
