import React, { useRef, useEffect } from 'react'

const Canvas = props => {
    const canvasRef = useRef(null);
    const draw = (canvas, context) => {
        canvas.onmousemove = function drawIfPressed (e) {
            var x = e.offsetX;
            var y = e.offsetY;
            var dx = e.movementX;
            var dy = e.movementY;
            if (e.buttons > 0) {
              context.beginPath();
              context.moveTo(x, y);
              context.lineTo(x - dx, y - dy);
              context.stroke();
              context.closePath();
            }
          };
    }
    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        draw(canvas, context);
    }, [draw])

  return <canvas width='450' height='450' ref={canvasRef} {...props}/>
}

export default Canvas;
