import { useEffect } from "react";
import React, { useRef, useState } from 'react';
import * as tf from "@tensorflow/tfjs";
import * as facemesh from "@tensorflow-models/facemesh";
import FacePaint from './FacePaint';

import styles from './Camera.module.css';

function addScript( url) {

        const script = document.createElement('script');

        script.src = url;
        script.async = true;
        document.body.appendChild(script);

}; 
export let faceCanvas;

const Camera = props => {
    const canvasRef = useRef(null);
    const videoRef = useRef(null);
    let model, w, h;
    let entries = [{
		handle: 'Chhau Mask',
		url: 'https://en.wikipedia.org/wiki/Chhau_mask',
		entry: './src/images/cherial-woman-mask.jpg'//,
		//background: 'hsl(35, 60%, 50%)'
	}];
    useEffect(()=>{
        const canvas = canvasRef.current;
        const video = videoRef.current;
        //const facemesh = require('@tensorflow-models/facemesh');
        async function renderPredictions(t) {
            requestAnimationFrame(renderPredictions);
            const predictions = await model.estimateFaces(video);

            if (predictions.length > 0) {
                for	(let i = 0; i < predictions.length; i++) {
                    const positionBufferData = predictions[0].scaledMesh.reduce((acc, pos) => acc.concat(pos), []);
                    if (!faceCanvas) {
                        const props = {
                            id: 'faceCanvas',
                            textureFilePath: entries[0].entry,
                            w,
                            h
                        }
                        faceCanvas = new FacePaint(props);
                        return;
                    }
                    faceCanvas.render(positionBufferData);
                }
            }
        }
        async function fetchCam () {

            const stream = await navigator.mediaDevices.getUserMedia({
				video: true,
				audio: false
			});
            video.srcObject = stream;
            await new Promise(function (res) {
				video.onloadedmetadata = function () {
					//w = video.videoWidth;
					//h = video.videoHeight;
					res();
				}
			});
            model = await facemesh.load({
                maxContinuousChecks: 5,
                detectionConfidence: 0.9,
                maxFaces: 1,
                iouThreshold: 0.3,
                scoreThreshold: 0.75
            });
            //renderPredictions();


			//video.height = h;
			//video.width = w;
        }
        fetchCam()
        video.play();


    }, []);
    return <div className={styles.camera}>
        <div>Camera</div>
        <video ref={videoRef} muted={true} playsInline={true}  className={styles.webcam} autoPlay={true} ></video>
        <canvas className={styles.faceCanvas} tabIndex="1"  ref={canvasRef} {...props}></canvas>
    </div>;
};
export default Camera;
