import React, { useRef, useEffect, Fragment, useState } from 'react';
import Palette from '../Palette/Palette';
import Buttons from '../Buttons';
import styles from '../Gallery.module.css';

const draw = (canvas, context, color, mode) => {
    canvas.onmousemove = function drawIfPressed (e) {
        let x = e.offsetX;
        let y = e.offsetY;
        let dx = e.movementX;
        let dy = e.movementY;
        context.lineCap = "round";
        context.lineWidth = 8;
        context.globalCompositeOperation = mode==='line'? "source-over": "destination-out";
        if (e.buttons > 0) {
          context.beginPath();
          context.moveTo(x, y);
          context.lineTo(x - dx, y - dy);
          context.stroke();
          context.strokeStyle = color;
          context.closePath();

        }
    };
}
const Canvas = props => {
    let [color, setColor] = useState('black');
    let [gallery, setImage] = useState([]);
    const canvasRef = useRef(null);
    const onChangeColor = color => {
        setColor(color);
    };
    const save = () => {
        const canvas = canvasRef.current
        let size = localStorage.length;
        let dataURL = canvas.toDataURL("image/png", 1.0);
        localStorage.setItem(`gallery${size+1}`, dataURL)
        addNewImg(dataURL); 
    };
    const clear = () => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
    };
    const eraser = () => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        //context.globalCompositeOperation = "destination-out";  
        draw(canvas,context,"rgb(255, 255, 255, 1)",'eraser')
        //onChangeColor("rgb(255, 255, 255, 1)");

    };
    const addNewImg = (dataURL) => {
        setImage([...gallery, <img key={dataURL} src={dataURL} className={styles.picture}/>]);
    }
    useEffect(() => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        draw(canvas, context, color,'line');
    }, [draw, color, gallery])

  return <Fragment>
    <div className={styles.gallery}>{gallery}</div>
    <canvas id='canvas' width='450' height='450' ref={canvasRef} {...props}/>
    <Buttons clear={clear} save ={save} eraser={eraser}/>
    <Palette onChangeColor={onChangeColor}></Palette>
  </Fragment> 
}
// <Buttons clear={clear(canvasRef.getContext('2d'))} />
export default Canvas;
