import React from 'react';

export default class Palette extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            pallete: [],
            //context: props.context
        };
        let canvas = <canvas id='canvas' width="450" height="450"></canvas>;
        //let colorPallete = <div id="palette"></div>;
        let context =  canvas.getContext("2d");

    }
    generatePalette = () => {
        for (var r = 0, max = 4; r <= max; r++) {
            for (var g = 0; g <= max; g++) {
              for (var b = 0; b <= max; b++) {
                let paletteBlock =<div className = 'colorButton'></div>
                paletteBlock.addEventListener('click', function changeColor(e) {
                  context.strokeStyle = e.target.style.backgroundColor;
                });

                paletteBlock.style.backgroundColor = (
                  'rgb(' + Math.round(r * 255 / max) + ", "
                  + Math.round(g * 255 / max) + ", "
                  + Math.round(b * 255 / max) + ")"
                );
                this.palette.push(paletteBlock);
              }
            }
        }
        return this.state.pallete;
    }



    render(){
        return <div id="palette">
            {this.generatePalette()}
        </div>
    }


}
