import React from 'react';

function Palette(props) {

  let palette = [];
  function generatePalette() {
    for (var r = 0, max = 4; r <= max; r++) {
      for (var g = 0; g <= max; g++) {
        for (var b = 0; b <= max; b++) {
          /*var paletteBlock = document.createElement('div');
          paletteBlock.className = 'colorButton';
          paletteBlock.addEventListener('click', function changeColor(e) {
            context.strokeStyle = e.target.style.backgroundColor;
          });*/
          let color = 'rgb(' + Math.round(r * 255 / max) + ", "
          + Math.round(g * 255 / max) +', '
          + Math.round(b * 255 / max) + ')';
          let colorBlock = <div className='colorButton'
          style = {{backgroundColor: color }}
          onClick = {() => props.onChangeColor(color)}
          key = {color} 
          />;
        }
      }
    }
  }


  return(
    <div id="palette">{palette}</div>
  )
}
