import React, { useRef, useEffect, Fragment, useState } from 'react';
import Palette from '../Palette/Palette';
import Buttons from '../Buttons';
import styles from '../Gallery.module.css';
let paths = [], points = [];
const draw = (canvas, color, mode) => {
    let context = canvas.getContext('2d');
    canvas.onmousemove = function drawIfPressed (e) {
        let x = e.offsetX;
        let y = e.offsetY;
        let dx = e.movementX;
        let dy = e.movementY;
        context.globalCompositeOperation = mode==='line'? "source-over": "destination-out";
        context.lineCap = "round";
        context.lineWidth = 8;
        if (e.buttons > 0) {
          context.beginPath();
          context.moveTo(x, y);
          context.lineTo(x - dx, y - dy);
          context.stroke();
          context.strokeStyle = color;
          context.closePath();
          //console.log(points)
        }
    };
    canvas.onmouseup = (e) => {
        paths.push(points);
    };
    canvas.onmousedown = ()=>{
        let previous = {x:mouse.x,y:mouse.y};
        let mouse = oMousePos(canvas, e);
        points = [];
        points.push({x:mouse.x,y:mouse.y})
    };
}
const drawPaths=(canvas)=> {
    let ctx = canvas.getContext('2d');
    ctx.clearRect(0,0,canvas.width,canvas.height);
  // draw all the paths in the paths array
    paths.forEach(path=>{
    ctx.beginPath();
    ctx.moveTo(path[0].x,path[0].y);  
    for(let i = 1; i < path.length; i++){
        ctx.lineTo(path[i].x,path[i].y); 
    }
    ctx.stroke();
    })
};

const Canvas = props => {
    let [color, setColor] = useState('black');
    let [gallery, setImage] = useState([]);
    //let [points, setPoint] = useState([]);
    const canvasRef = useRef(null);
    const onChangeColor = color => {
        setColor(color);
    };
    const save = () => {
        const canvas = canvasRef.current
        let size = localStorage.length;
        let dataURL = canvas.toDataURL("image/png", 1.0);
        localStorage.setItem(`gallery${size+1}`, dataURL)
        addNewImg(dataURL); 
    };
    const clear = () => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
        points=[];
    };
    const eraser = () => {
        const canvas = canvasRef.current
        draw(canvas,"rgb(255, 255, 255, 1)",'eraser');
    };
    const addNewImg = (dataURL) => {
        setImage([...gallery, <img key={dataURL} src={dataURL} className={styles.picture}/>]);
    }
    const undo = () => {
        const canvas = canvasRef.current
        paths.splice(-1,1);
        drawPaths(canvas);




    }
    useEffect(() => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d');
        draw(canvas, color,'line');
    }, [draw, color, gallery])

  return <Fragment>
    <div className={styles.gallery}>{gallery}</div>
    <canvas id='canvas' width='450' height='450' ref={canvasRef} {...props}/>
    <Buttons clear={clear} save ={save} eraser={eraser} undo={undo}/>
    <Palette onChangeColor={onChangeColor}></Palette>
  </Fragment> 
}
// <Buttons clear={clear(canvasRef.getContext('2d'))} />
export default Canvas;
